var Benchmark = require('benchmark');

var suite = new Benchmark.Suite;

var PATH = '/usr/local/bin/node';
var PATH_ARRAY = [PATH, PATH, PATH, PATH, PATH];

suite.add('Array.isArray with string', function () {
  Array.isArray(PATH);
});

suite.add('Array.isArray with array', function () {
  Array.isArray(PATH_ARRAY);
});

suite.add('typeof with string', function () {
  typeof PATH === 'string';
});

suite.add('typeof with array', function () {
  typeof PATH_ARRAY === 'string';
});

suite.on('cycle', function (event) {
  console.log(String(event.target));
})

suite.run();
